    this.clearHistoryAction = function () { };

    /** 默认历史数据存取Store */
    this.historyStore = null;

    /** 数据源 */
    this.dataSource = [];

    /** 自动设置尺寸的资源句柄 */
    this._autoLocResoure;

    /** body元素 */
    this._bodyDom;

    /** 根节点 */
    this.rootBox;

    /** 设置显示下拉框 */
    this._oneShow = false;

    var self = this;

    /** 事件定义获取焦点 */
    this.event_focus = function () {
      self.Open();
      if (typeof self.focusFun == 'function') {
        self.focusFun();
      }
    };

    /** 事件定义失去焦点 */
    this.event_blur = function () {
      /*setTimeout(function () {
      if (!self._oneShow) {
      self.Close();
      }
      self._oneShow = false;
      }, 200);*/
      if (typeof self.blurFun == 'function') {
        self.blurFun();
      }
    };

