      //                    this.cooling = true; //关闭冷却时间
      var num = parseInt(scope.disItemNum / 2);

      if (scope.type == 'list') {
        if (this.data.length == scope.disItemNum) {
          num = i;
        } else {
          num = i == 0 ? 0 : 1;
          //产品要求，最低处不能有空格
          if (this.size - i < this.disItemNum)
            num = (-1) * parseInt(this.size) + parseInt(this.disItemNum) + parseInt(i);
        }
      }

